/**
 * @title Radio
 */
interface RadioProps {
  /**
   * @hide
   */
  id: "radio";
  name: string;
}

function Radio({ name }: RadioProps) {
  return (
    <input
      type="radio"
      name={name}
      class="radio h-8 w-8 lg:w-9 lg:h-9 radio-primary elem absolute rounded-full checked:!bg-black hover:border-white  border-white dark:hover:border-black dark:border-black"
      checked
    />
  );
}

/**
 * @title CheckBox
 */
interface CheckBoxProps {
  /**
   * @hide
   */
  id: "checkbox";
}

function CheckBox({}: CheckBoxProps) {
  return (
    <input
      type="checkbox"
      checked
      class="checkbox border w-10 h-10 lg:w-14 lg:h-14 elem absolute rounded-xl border-white dark:border-black"
    />
  );
}

/**
 * @title Toggle
 */
interface ToggleProps {
  /**
   * @hide
   */
  id: "toggle";
}

function Toggle({}: ToggleProps) {
  return (
    <input
      type="checkbox"
      style={`--tglbg: black; --handleoffset: 50px;`}
      class="toggle w-[80px] h-[40px] lg:w-[100px] lg:h-[50px] !text-white rounded-xl elem absolute"
      checked
    />
  );
}

/**
 * @title Input
 */
interface InputProps {
  /**
   * @hide
   */
  id: "input";
}

function Input({}: InputProps) {
  return (
    <input
      type="text"
      class="elem absolute input input-bordered rounded-xl input-sm lg:input-lg"
      placeholder="Type here"
    />
  );
}

interface ButtonProps {
  /**
   * @hide
   */
  id: "button";
  label: string;
  color: string;
  backgroundColor: string;
}

const toVar = (cssVar: string) =>
  `oklch(var(${cssVar}, var(--b2)) / var(--tw-bg-opacity))`;

function Button({ label, color, backgroundColor }: ButtonProps) {
  const bg = backgroundColor.includes("-")
    ? toVar(backgroundColor)
    : backgroundColor;
  const clr = color.includes("-") ? toVar(color) : color;

  return (
    <button
      style={`background-color: ${bg}; color: ${clr};`}
      class="elem btn border-none absolute btn-sm lg:btn-lg rounded-xl hover:brightness-[85%]"
    >
      {label}
    </button>
  );
}

/**
 * @title Slider
 */
interface SliderProps {
  /**
   * @hide
   */
  id: "slider";
}

function Slider({}: SliderProps) {
  return (
    <input
      type="range"
      style={`--range-shdw: black; --fallback-bc: #C9CFCF; --rounded-box: 6px;`}
      class="elem absolute range w-[200px] lg:w-[300px] border-white"
      min="0"
      max="100"
      value="40"
    />
  );
}

type AnimationElement =
  | SliderProps
  | ButtonProps
  | InputProps
  | ToggleProps
  | CheckBoxProps
  | RadioProps
  | ToggleDarkModeProps;

interface TopButton {
  label: string;
  icon: AvailableIcons;
  url: string;
}

interface Header {
  date?: string;
  loc?: string;
  fullLoc?: string;
}

export interface Props {
  animationElements: AnimationElement[];
  /**
 * @description Mobile Elements
 */
  animationElementsMobile: AnimationElement[];
  /**
 * @description Select Gravity Level - 1(super slow) to 7(super fast) | Default is 4(Earths Gravity)
 */
  /** @default 4 */
  gravitySensation?: 1 | 2 | 3 | 4 | 5 | 6 | 7 ;
  infoPanel: {
    scheduleHeader?: Header;
    schedule: ScheduleProps;
    slideBanner: SlideBannerProps;
    socialLinks: SocialLinksProps;
    topButtons: TopButton[];
    ctaText: string;
  };
  emailInput: {
    successMessage?: string;
    errorMessage?: string;
    placeholder?: string;
  };
}

const AnimatedElementMap: Record<
  AnimationElement["id"],
  // deno-lint-ignore no-explicit-any
  (props: any) => JSX.Element
> = {
  button: Button,
  checkbox: CheckBox,
  darkmode: ToggleDarkMode,
  input: Input,
  radio: Radio,
  slider: Slider,
  toggle: Toggle,
};

export const loader = async (
  props: Props,
  req: Request,
  ctx: AppContext,
) => {

  const device = ctx.device;

  return{
    ...props,
    isMobile: device
  } 
}

